package org.injiri.healthyfarmer.adapters;

import android.content.Context;
import android.view.LayoutInflater;
import android.view.View;
import android.view.ViewGroup;
import android.widget.ImageView;
import android.widget.TextView;

import com.bumptech.glide.Glide;
import com.smarteist.autoimageslider.SliderViewAdapter;

import org.injiri.healthyfarmer.R;

public class MessageCardAdapter extends SliderViewAdapter<MessageCardAdapter.MessageCardHolder> {

    private Context context;

    public MessageCardAdapter(Context context) {
        this.context = context;
    }


    @Override
    public MessageCardHolder onCreateViewHolder(ViewGroup parent) {
        View inflate = LayoutInflater.from(parent.getContext()).inflate(R.layout.slider_item, null);
        return new MessageCardHolder(inflate);
    }

    @Override
    public void onBindViewHolder(MessageCardHolder viewHolder, int position) {
        viewHolder.textViewDescription.setText("This is slider item " + position);

        switch (position) {
            case 0:
                Glide.with(viewHolder.itemView)
                        .load("https://images.pexels.com/photos/218983/pexels-photo-218983.jpeg?auto=compress&cs=tinysrgb&dpr=2&h=750&w=1260")
                        .into(viewHolder.imageViewBackground);
                break;
            case 1:
                Glide.with(viewHolder.itemView)
                        .load("https://images.pexels.com/photos/747964/pexels-photo-747964.jpeg?auto=compress&cs=tinysrgb&h=750&w=1260")
                        .into(viewHolder.imageViewBackground);
                break;
            case 2:
                Glide.with(viewHolder.itemView)
                        .load("data:image/jpeg;base64,/9j/4AAQSkZJRgABAQAAAQABAAD/2wCEAAkGBxMTEhUTExMWFhUXGCAYGRgXGB" +
                                "4eGRseGxsdICAeISAhICogGx4lGx8fIjEhJSkrLi4uISAzODMtNyotLisBCgoKDg0OGxAQGy0lICUvLS8vLS0tLy0vLS0tLS" +
                                "0tLS0tLS0tLS0tLS0tLS0tLS0tLS0tLS0tLS0tLS0tLS0tLf/AABEIALYBFQMBIgACEQEDEQH/xAAbAAACAgMBAAAAAAAAAAAAAAAEBQMGAAIHAf/E" +
                                "AEIQAAIBAwIFAgQCBwcEAgEFAAECEQMSIQAxBAUiQVETYQYycYFCkRQjUqHB0fAHFWJykrHhM0NT8YLSohYkNHPC/8QAGgEAAwEBAQEAAAAAAAAAAAAAAQIDAAQ" +
                                "FBv/EACkRAAICAgIDAAICAQUBAAAAAAABAhEDIRIxE0FRImEEMnEVQpGh8BT/2gAMAwEAAhEDEQA/AA/gFl9KoSyDqG7AHE++rWvEU+9RP9Q1Q/g7gbqTmPx/tRuT7HVm" +
                                "")
                        .into(viewHolder.imageViewBackground);
                break;
            default:
                Glide.with(viewHolder.itemView)
                        .load("https://www.researchgate.net/profile/Kermit_Davis/publication/264867000/figure/fig3/AS:295988955762696@1447580674966" +
                                "/Korean-farmer-carrying-heavy-spray-tank-while-spraying-fields-with-pesticides-Colours.png")
                        .into(viewHolder.imageViewBackground);
                break;

        }
    }

    @Override
    public int getCount() {
        return 5; // return total number of cards to display
    }

    public class MessageCardHolder extends SliderViewAdapter.ViewHolder {
        View itemView;
        ImageView imageViewBackground;
        TextView textViewDescription;

        public MessageCardHolder(View itemView) {
            super(itemView);
            imageViewBackground = itemView.findViewById(R.id.iv_auto_image_slider);
            textViewDescription = itemView.findViewById(R.id.tv_auto_image_slider);
            this.itemView = itemView;
        }
    }
}
